mapper.select:
select
 s.stu_name,
  s.stu_num,
f.fac_name,
  d.dept_name,
 g.g_year,
  g.g_class,
 s.stu_ID
from students s
LEFT JOIN faculty f ON s.stu_faculty = f.fac_num
LEFT JOIN department d ON s.dept = d.dept_num
LEFT JOIN grade g ON s.grade = g.g_id


        and s.stu_num = #{stu_num}


        and s.stu_ID = #{stu_ID}


        and s.stu_name = #{stu_name}


        and s.stu_faculty = #{stu_faculty}


        and s.dept = #{dept}


        and s.grade = #{grade}

select * from student


          and stu_num = #{stu_num}


         and stu_ID = #{stu_ID}


          and stu_name like '%${stu_name}%'


         and stu_faculty = #{stu_faculty}


         and stu_name like '%${stu_name}%'
